#include <iostream>
#include <vector>

int findFloorNum(std::string& instructions) {
	int currentFloor = 0;

	//( - go up 1 floor
	//) - go down 1 floor

	for (auto it = instructions.cbegin(); it != instructions.cend(); it++) {
		if (*it == '(') {
			currentFloor++;
		}
		else if (*it == ')') {
			currentFloor--;
		}
	}
	return currentFloor;
}

int findCharPosition(std::string& instructions) {
	int currentFloor = 0;
	int charPos = 0;

	for (int i = 0; instructions[i] != '\0' && currentFloor != -1; i++, charPos++) {
		if (instructions[i] == '(') {
			currentFloor++;
		}
		else if (instructions[i] == ')') {
			currentFloor--;
		}
	}
	return charPos;
}

